import React, { useState } from "react";
import { Link } from "react-router-dom";
import { projects } from "../dummyData/projects";
import ProjectList from "../components/ProjectList";

export default function ProjectsPage() {
  const [search, setSearch] = useState("");

  function searchOnChange(event) {
    setSearch(event.target.value);
  }
  const filteredLinks = (search) => {
    const lowerSearch = search.toLowerCase();
    return projects.filter((link) => {
      return (
        link.name.toLowerCase().indexOf(lowerSearch) > -1 ||
        link.url.toLowerCase().indexOf(lowerSearch) > -1 ||
        link.tags
          .map((tag) => {
            return (
              tag.name.toLowerCase().indexOf(lowerSearch) >
              -1
            );
          })
          .indexOf(true) > -1
      );
    });
  };

  // given search, will filter out the projects
  return (
    <div>
      <h1>Projects Page</h1>
      <input
        type="text"
        value={search}
        placeholder="Search"
        onChange={searchOnChange}
      />
      <ProjectList projects={projects} />
    </div>
  );
}
