/**********************************************
 * Accordion Component
 * ==================================
 * - It will accept an array of objects
 * - Render out each object accordingly
 ***********************************************/
import "../assets/Accordion.css";
import React from "react";
import ReactDOM from "react-dom";

class Panel extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      height: 0,
    };
  }

  componentDidMount() {
    window.setTimeout(() => {
      const el = ReactDOM.findDOMNode(this);
      const height =
        el.querySelector(".panel__inner").scrollHeight;
      this.setState({
        height,
      });
    }, 333);
  }

  render() {
    const {
      //   label,
      //   content,
      //   array of objects
      id,
      institution,
      title,
      date,
      type,
      description,
      activeTab,
      index,
      activateTab,
    } = this.props;
    const { height } = this.state;
    const isActive = activeTab === index;
    const innerStyle = {
      height: `${isActive ? height : 0}px`,
    };

    return (
      <div
        className="panel"
        role="tabpanel"
        aria-expanded={isActive}
      >
        <button
          className="panel__label"
          role="tab"
          onClick={activateTab}
        >
          {institution}
        </button>
        <div
          className="panel__inner"
          style={innerStyle}
          aria-hidden={!isActive}
        >
          <p className="panel__content">{title}</p>
          <p className="panel__content">
            {date} {type} {description}
          </p>
        </div>
      </div>
    );
  }
}

// Job Accordion
