import React from "react";
import { Link } from "react-router-dom";
import { resumes } from "../dummyData/resumes";
import ReactDOM from "react-dom";
import "../assets/Accordion.css";
/**********************************************
 * Resume List Component
 * ==================================
 * This component accepts an array of objects, rendering out each object (including the navbar react link)
 * We gave it a function, and then rendered it inside the return block
 ***********************************************/
class Panel extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      height: 0,
    };
  }

  componentDidMount() {
    window.setTimeout(() => {
      const el = ReactDOM.findDOMNode(this);
      const height =
        el.querySelector(".panel__inner").scrollHeight;
      this.setState({
        height,
      });
    }, 333);
  }

  render() {
    const {
      //   label,
      //   content,
      //   array of objects
      id,
      institution,
      position,
      date,
      type,
      description,
      activeTab,
      index,
      activateTab,
    } = this.props;
    const { height } = this.state;
    const isActive = activeTab === index;
    const innerStyle = {
      height: `${isActive ? height : 0}px`,
    };

    return (
      <div
        className="panel"
        role="tabpanel"
        aria-expanded={isActive}
      >
        <button
          className="panel__label"
          role="tab"
          onClick={activateTab}
        >
          {institution}
        </button>
        <div
          className="panel__inner"
          style={innerStyle}
          aria-hidden={!isActive}
        >
          <p className="panel__content">{position}</p>
          <p className="panel__content">
            {date} {type} {description}
          </p>
        </div>
      </div>
    );
  }
}

// Job Accordion
// Certification Accordion
// Education Accordion


class Accordion extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      activeTab: 0,
    };

    this.activateTab = this.activateTab.bind(this);
  }

  activateTab(index) {
    this.setState((prev) => ({
      activeTab: prev.activeTab === index ? -1 : index,
    }));
  }

  render() {
    const { resumes } = this.props;
    const { activeTab } = this.state;
    return (
      <div className="accordion" role="tablist">
        {resumes.map((panel, index) => (
          <Panel
            key={index}
            activeTab={activeTab}
            index={index}
            {...panel}
            activateTab={this.activateTab.bind(null, index)}
          />
        ))}
      </div>
    );
  }
}

// const App = document.querySelector("#app");

// ReactDOM.render(<Accordion panels={panels} />, App);

function ResumeList(props) {
  function renderResumes() {
    return props.resumes.map((resume, index) => {
      return (
        <div key={resume.id} className="accordion-item">
          <h2
            className="accordion-header"
            id="panelsStayOpen-headingTwo"
          >
            <button
              className="accordion-button collapsed"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#panelsStayOpen-collapseTwo"
              aria-expanded="false"
              aria-controls="panelsStayOpen-collapseTwo"
            >
              <div className="row">
                <div className="col-6 align-items-start">
                  {resume.institution}
                </div>
                <div className="col-6 align-items-end">
                  {resume.date}
                </div>
              </div>
            </button>
          </h2>
          <div
            id="panelsStayOpen-collapseTwo"
            className="accordion-collapse collapse"
            aria-labelledby="panelsStayOpen-headingTwo"
          >
            <div className="accordion-body">
              <strong>
                This is the second item's accordion body.
              </strong>{" "}
              It is hidden by default, until the collapse
              plugin adds the appropriate classNamees that
              we use to style each element. These
              classNamees control the overall appearance, as
              well as the showing and hiding via CSS
              transitions. You can modify any of this with
              custom CSS or overriding our default
              variables. It's also worth noting that just
              about any HTML can go within the{" "}
              <code>.accordion-body</code>, though the
              transition does limit overflow.
              {resume.institution}
              {resume.position}
            </div>
          </div>
        </div>
      );
    });
  }
  return (
    <div>
      <h3>resume List</h3>
      {/* <TestAccordian /> */}
      <Accordion resumes={resumes} />
      {renderResumes()}
    </div>
  );
}

export default ResumeList;
